					<?php $menuLocations = get_nav_menu_locations(); ?>
                        <?php $menuID = $menuLocations['top']; ?>
                        <?php $items = wp_get_nav_menu_items($menuID); ?>
                            <?php foreach( $items as $item){ ?>

                                <?php if($item->menu_item_parent == 0){?>

                                    <li class="f__menu-link">
                                        <a class="f__top-link" href="<?php echo $item->url; ?>"><?php echo $item->title; ?></a>
                                        
                                        <?php 
                            
                                            $subMenu = array();
                                            if($item->object == "page"){
                                                $links = get_posts( array(
                                                    'post_parent' => $item->object_id,
                                                    "post_type" => "page",
                                                    "posts_per_page" => -1,
													'meta_query' => array(
														'book_color' => array(
															'key'     => 'sorting',
															'value'   => array( 0, 100 ),
															'type'    => 'numeric',
															'compare' => 'BETWEEN'
														)
													),
													"orderby" => "sorting",
													"order" => "ASC"
                                                ) );

                                                foreach($links as $link){
                                                    $subMenu[] = array(
                                                        "url" => get_permalink($link->ID),
                                                        "title" => $link->post_title,
                                                        "img" => get_field("menu-img",$link->ID)
                                                    );
                                                }
                                            }else if($item->object == "solutions"){
                                                $links = get_posts( array(
                                                    "post_type" => $item->object,
                                                    "posts_per_page" => -1
                                                ) );

                                                foreach($links as $link){
                                                    $subMenu[] = array(
                                                        "url" => get_permalink($link->ID),
                                                        "title" => $link->post_title,
                                                    );
                                                }
                                            }
                                            
                                            if(count($subMenu) > 0){
                                        ?>
                                        <ul>
                                                
                                            <?php 
                                            $total = count($subMenu);

                                            if($total<=12){
                                                $number = 6;
                                            }else{
                                                $number = round($total/2);
                                            }
                                            ?>
                                                        
                                            <?php
                                            foreach($subMenu as $key=>$link){
                                                if($key < $number){
                                                ?>

                                                    <li>
                                                        <a href="<?php echo $link['url']; ?>">	
                                                            <?php echo $link['title']; ?>
                                                        </a>
                                                    </li>

                                                <?php } ?>

                                            <?php } ?>

                                        </ul>

                                        <?php } ?> 	

                                    </li>
                
                            <?php } ?>

                        <?php } ?>
